  }

  #logo(@logo) {
    ytd-topbar-logo-renderer.style-scope a svg > g,
    svg.ytd-consent-bump-v2-lightbox > g:nth-child(1) {
      path[fill="#FF0000"] {
        fill: @accent-color !important;
      }
      path[fill="white"] {
        fill: @base !important;
      }
    }
