    &,
    html,
    html[dark],
    html[dark][dark],
    &[dark],
    &[light],
    [light],
    html[light],
    html[light][light] {
      /* disable */
      --yt-spec-black-pure-alpha-80: (null) !important;
      --yt-spec-black-pure-alpha-60: (null) !important;
      --yt-spec-black-1-alpha-98: (null) !important;
