      --color-red-7: @accent-color;
      --color-red-8: @accent-color;
      --color-red-9: @accent-color;
      --color-red-10: @accent-color;
      --color-red-11: @peach;
      --color-red-12: @peach;
      --color-twitter: @blue;
      --color-facebook: @blue;
      --color-reddit: @peach;
      --color-snapchat: @yellow;
      --color-instagram: @blue;
      --color-youtube: @red;
      --color-paypal: @blue;
      --color-paypal-blue: @blue;
      --color-paypal-yellow: @yellow;
      --color-venmo: @blue;
      --color-vk: @blue;
      --color-amazon: @yellow; //background: @surface0;
